    @Override
    public int getMoneyFromView(){
        return (int) this.walletValue_Spinner.getValue();
    }

    @Override
    public void setMoneyToView(double inputMoney) {
        this.walletValue_ProgressBar.setValue((int) inputMoney);
        this.walletValue_Spinner.setValue((int) inputMoney);
        
    }

